require_once ('php1board.php');

// <주의>파일이름과 클래스이름을 일치해야...
class skin2blog extends form1board {
	// 환경설정 초기값 재정의
	function resetconfig() {
		global $sysconf, $envconf;
		
		$sysconf ["skin_image"] = "$sysconf[home_skins]/" . get_class ( $this );
		
		$envconf ["backimage"] = $sysconf ["skin_image"] . '/skin2_35.gif';
		$envconf ["pageline"] = "5"; // 한 페이지당 출력할 목록 갯수
		$envconf ["show_category"] = 1; // 카테고리
	}
	
	// 전체 목록 보이기
	function testboardlist() {
		global $envconf, $btn;
		global $year, $month;
		
		$this->htmlheader ();
		
		echo '<table id="calnav" width="175" border="0" cellspacing="0" cellpadding="3">', "<tr>\n<td>";
		$this->calendar ( $year, $month );
		echo "</tr>\n<tr>\n</table>";
		
		$this->htmlbottom ();
	}
	function calendar($year, $month) {
		$day_color = array (
				'red',
				'black',
				'black',
				'black',
				'black',
				'black',
				'blue' 
		);
		$num_dayname = array (
				0,
				1,
				2,
				3,
				4,
				5,
				6 
		);
		$han_dayname = array (
				'일',
				'월',
				'화',
				'수',
				'목',
				'금',
				'토' 
		);
		$eng_dayname = array (
				'S',
				'M',
				'T',
				'W',
				'T',
				'F',
				'S' 
		);
		
		// Give defaults for the month and day values if they were invalid
		$cy = if_empty ( $year, yy () );
		$cm = if_empty ( $month, mm () );
		$todate = date ( "Y/m/d" );
		
		// Beginning and end of this month
		$bom = mktime ( 0, 0, 1, $cm, 1, $cy );
		
		// Link to previous month (but do not link to too early dates)
		$lm = mktime ( 0, 0, 1, $cm, 0, $cy );
		$prev_link = "$this->prog?db=$this->db&mode=list&page=$this->page&id=$this->id&ff=$this->ff&fw=$this->fw" . strftime ( '&amp;month=%m&amp;year=%Y', $lm );
		
		// Link to next month (but do not link to too early dates)
		$nm = mktime ( 0, 0, 1, $cm + 1, 1, $cy );
		$next_link = "$this->prog?db=$this->db&mode=list&page=$this->page&id=$this->id&ff=$this->ff&fw=$this->fw" . strftime ( '&amp;month=%m&amp;year=%Y', $nm );
		
		// 해당월에 내용 읽기
		$table = $this->obj->findfile ( $this->db, "in_date", date ( "Y/m", $bom ) );
		
		// Print out navigation links for previous and next month
		$style = 'cursor:hand;font-size:11px;';
		echo '<table id="calnav" width="100%" border="0" cellspacing="0" cellpadding="3">', '<tr style="font-size:11px">', '<td align="left" width="33%" onclick="location.href=\'', $prev_link, '\'" style=', $style, '>◀</td>', '<td align="center" width="33%">', strftime ( '<b>%B,%Y</b></td>', $bom ), '<td align="right" width="33%" onclick="location.href=\'', $next_link, '\'" style=', $style, '>▶</td>', '</tr></table>', "\n";
		
		// Begin the calendar table
		echo '<table id="cal" width="100%" border="0" cellspacing="0" cellpadding="3">', '<tr>', "\n";
		
		// Print out headers for weekdays
		for($i = 0; $i < 7; $i ++) {
			echo '<td height=13 align="center" valign="top">';
			echo '<font style="font-size:11px" color=', $day_color [$i], '>', $han_dayname [$i], '</font>';
			echo '</td>';
		} // end for
		
		echo "</tr>\n<tr>";
		
		// Generate the requisite number of blank days to get things started
		for($days = $i = date ( "w", $bom ); $i > 0; $i --)
			echo '<td class="notaday">&nbsp;</td>';
			
			// Print out all the days in this month
		for($i = 1; $i <= date ( "t", $bom ); $i ++) {
			// //////////////////단시작////////////////////
			$font_color = $day_color [$days % 7];
			$style = 'font-size:11px;';
			$alink = '#';
			
			// 오늘 표시
			$cal_date = date ( "Y/m/", $bom ) . sprintf ( "%02d", $i );
			if ($todate == $cal_date)
				$style .= 'font-weight:bold;';
			if (is_array ( $table ) && array_key_exists ( $cal_date, $table )) {
				$style .= 'cursor:hand;text-decoration:underline;';
				$alink = "$this->prog?db=$this->db&mode=list&page=$this->page&id=$this->id&ff=in_date&fw=$cal_date&amp;cd=$i&amp;month=$cm&amp;year=$cy";
			} // end if
			  
			// Print out day number and all events for the day
			echo '<td align="center" onclick="location.href=\'', $alink, '\'" style=', $style, '><font color=', $font_color, '>', $i, '</font></td>';
			
			// //////////////////단끝////////////////////
			// Break HTML table row if at end of week
			if (++ $days % 7 == 0)
				echo "</tr>\n<tr>";
		} // end for
		  
		// Generate the requisite number of blank days to wrap things up
		for(; $days % 7; $days ++)
			echo '<td class="notaday">&nbsp;</td>';
			
			// End HTML table of events
		echo "</tr>\n</table>\n";
	}
}
